import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:qr_flutter/qr_flutter.dart';class QrcodeScreen extends StatefulWidget {  static String id = "qrcode_screen";  @override  _QrcodeScreenState createState() => _QrcodeScreenState();}class _QrcodeScreenState extends State<QrcodeScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Color(0xFF000000),      body: Column(        children: [          Center(            child: Container(              margin: EdgeInsets.symmetric(vertical: 50,horizontal: 0),                child: Image.asset('images/logo.png'),              height: 75,            ),          ),          Center(              child: Text("Scannez ce Qr-code à l'entrée de chaque salle", style: TextStyle(color: Colors.white),),          ),          Container(            width: 70,              child: Divider(                color: Colors.white,                thickness: 1.0,          )),          Center(            child: Container(              child: QrImage(                data: "1234567890",                version: QrVersions.auto,                size: 250.0,                backgroundColor: Colors.black,                foregroundColor: Colors.white,                embeddedImage: AssetImage('images/logo-qr.png'),                embeddedImageStyle: QrEmbeddedImageStyle(                  size: Size(60, 60),                ),                errorStateBuilder: (cxt, err) {                  return Container(                    child: Center(                      child: Text(                        "Une erreur est s'ervenue, Veuillez nous appeler sur le numéro suivant 0700907802 ou réssayer ultérieurement",                        textAlign: TextAlign.center,                      ),                    ),                  );                },              ),            ),          ),          Center(            child: Text("Nombre de séances restantes: 7", style: TextStyle(color: Colors.white),),          ),          Align(            alignment: Alignment.bottomRight,            child: Container(              constraints: BoxConstraints(maxWidth: 200.0, minHeight: 30.0),              margin: EdgeInsets.only(top:10,right: 25),              child: RaisedButton(                onPressed: () {},                color: Color(0xFFc21e53),                child: Padding(                  padding: EdgeInsets.all(0),                  child: Container(                    alignment: Alignment.center,                    child: Row(                      mainAxisAlignment: MainAxisAlignment.spaceBetween,                      children: <Widget>[                        Text(                          'Toutes les salles',                          style: TextStyle(                            fontSize: 15,                            color: Colors.white,                          ),                        ),                        Icon(                          Icons.arrow_forward,                          color: Colors.white,                        )                      ],                    ),                  ),                ),              ),            ),          ),        ],      ),    );  }}