import 'package:flash_chat/screens/home_screen.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:firebase_auth/firebase_auth.dart';class RegistrationScreen extends StatefulWidget {  static String id='registration_screen';  @override  _RegistrationScreenState createState() => _RegistrationScreenState();}class _RegistrationScreenState extends State<RegistrationScreen> {  final _auth = FirebaseAuth.instance;  String email;  String password;  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Color(0xFF),      body: Padding(        padding: EdgeInsets.symmetric(horizontal: 24.0),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          crossAxisAlignment: CrossAxisAlignment.stretch,          children: <Widget>[            Container(              height: 200.0,              child: Image.asset('images/logo.png'),            ),            SizedBox(              height: 48.0,            ),            TextField(              onChanged: (value) {                email= value;              },              keyboardType: TextInputType.emailAddress,              textAlign: TextAlign.center,              decoration: InputDecoration(                hintText: 'Enter your email',                contentPadding: EdgeInsets.symmetric(vertical: 10.0, horizontal: 20.0),                border: OutlineInputBorder(                  borderRadius: BorderRadius.all(Radius.circular(32.0)),                ),                enabledBorder: OutlineInputBorder(                  borderSide: BorderSide(color: Color(0xFF1d1e33), width: 1.0),                  borderRadius: BorderRadius.all(Radius.circular(32.0)),                ),                focusedBorder: OutlineInputBorder(                  borderSide: BorderSide(color: Color(0xFF1d1e33), width: 2.0),                  borderRadius: BorderRadius.all(Radius.circular(32.0)),                ),              ),            ),            SizedBox(              height: 8.0,            ),            TextField(              onChanged: (value) {                password= value;              },              //obscureText: true,              textAlign: TextAlign.center,              decoration: InputDecoration(                hintText: 'Enter your password',                contentPadding:                    EdgeInsets.symmetric(vertical: 10.0, horizontal: 20.0),                border: OutlineInputBorder(                  borderRadius: BorderRadius.all(Radius.circular(32.0)),                ),                enabledBorder: OutlineInputBorder(                  borderSide: BorderSide(color:Color(0xFF1d1e33), width: 1.0),                  borderRadius: BorderRadius.all(Radius.circular(32.0)),                ),                focusedBorder: OutlineInputBorder(                  borderSide: BorderSide(color: Color(0xFF1d1e33), width: 2.0),                  borderRadius: BorderRadius.all(Radius.circular(32.0)),                ),              ),            ),            SizedBox(              height: 24.0,            ),            Padding(              padding: EdgeInsets.symmetric(vertical: 16.0),              child: Material(                color: Color(0xFFc21e53),                borderRadius: BorderRadius.all(Radius.circular(30.0)),                elevation: 5.0,                child: MaterialButton(                  onPressed: () async {                    try {                      final newUser = await _auth.createUserWithEmailAndPassword(email: email, password: password);                      if( newUser != null){                        print('not null');                        Navigator.pushNamed(context, HomeScreen.id);                      }                      else {                        print("awili new user is null");                      }                    }                    catch(e) {                      print(e);                    }                  },                  minWidth: 200.0,                  height: 42.0,                  child: Text(                    'Register',                    style: TextStyle(color: Colors.white),                  ),                ),              ),            ),          ],        ),      ),    );  }}